import { useEffect, useState } from 'react';
import { apiInstance } from '../api/axios';

export default function CommentsSection({ postId }) {
  const [comments, setComments] = useState([]);
  const [text, setText] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    apiInstance.get(`posts/${postId}/comments`)  // Remove leading slash
        .then(res => setComments(res.data))
        .catch(err => setError("Failed to load comments"));
  }, [postId]);

  const addComment = async () => {
    try {
      // Updated to match your backend endpoint
      await apiInstance.post(`posts/${postId}/comment`, { content: text });  // Changed to /comment singular
      setText("");
      const res = await apiInstance.get(`posts/${postId}/comments`);
      setComments(res.data);
    } catch (error) {
      console.error('Comment error:', error);
      setError("Failed to add comment");
    }
  };

  return (
    <div className="mt-4 border-t pt-2">
      <h3 className="font-semibold mb-2">Comments</h3>
      {comments.map((c, i) => (
        <p key={i} className="text-sm text-gray-700 border-b py-1">{c.content}</p>
      ))}
      <div className="mt-2 flex gap-2">
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="Write a comment"
          className="flex-1 p-2 border rounded"
        />
        <button onClick={addComment} className="px-4 py-2 bg-blue-500 text-white rounded">Post</button>
      </div>
    </div>
  );
}
