// src/pages/HomePage.jsx
import { useEffect, useState } from 'react';
import api from '../api/axios';
import PostCard from '../components/PostCard';
import { useNavigate } from 'react-router-dom';
export default function HomePage() {
  const [posts, setPosts] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [categories, setCategories] = useState([]);
  const [tags, setTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedTag, setSelectedTag] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Update the auth checking useEffect
useEffect(() => {
  const checkAuth = async () => {
      console.log("=== Auth Check ===");
      const token = localStorage.getItem("token");
      console.log("Token present:", !!token);
      
      if (!token) {
        console.log("No token found - redirecting to login");
        setCurrentUser(null);
        navigate('/login'); // Redirect to login
        return;
    }

      try {
          // Verify token format
          const parts = token.split('.');
          if (parts.length !== 3) {
              throw new Error('Invalid token format');
          }

          // Decode payload
          const payload = JSON.parse(atob(parts[1]));
          console.log("Token payload:", payload);

          // Check expiration
          if (payload.exp && payload.exp * 1000 < Date.now()) {
              throw new Error('Token expired');
          }

          setCurrentUser(payload.sub);
          console.log("User authenticated:", payload.sub);
      } catch (error) {
          console.error("Auth error:", error.message);
          localStorage.removeItem("token");
          setCurrentUser(null);
      }
      console.log("=== Auth Check End ===");
  };

  checkAuth();
}, []);

// Update the posts fetching useEffect
useEffect(() => {
  const fetchPosts = async () => {
      setLoading(true);
      let url = "posts";
      if (selectedCategory) url = `posts/category/${selectedCategory}`;
      else if (selectedTag) url = `posts/tag/${selectedTag}`;

      try {
          console.log("=== Fetching Posts ===");
          console.log("URL:", url);
          console.log("Auth headers:", api.defaults.headers.common);
          
          const response = await api.get(url);
          console.log("Posts received:", response.data);
          setPosts(response.data);
      } catch (error) {
          console.error("Posts fetch error:", {
              message: error.message,
              status: error.response?.status,
              data: error.response?.data
          });
          alert(`Error: ${error.response?.data?.message || 'Failed to load posts'}`);
      } finally {
          setLoading(false);
      }
  };

  fetchPosts();
}, [selectedCategory, selectedTag]);
  // Load current user from JWT token
 

  // Fetch categories and tags once at component mount
  useEffect(() => {
    api.get("categories").then(res => setCategories(res.data));
    api.get("tags").then(res => setTags(res.data));
  }, []);

  // Handler for when a category is selected
  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setSelectedTag(""); // reset tag filter
  };

  // Handler for when a tag is selected
  const handleTagChange = (e) => {
    setSelectedTag(e.target.value);
    setSelectedCategory(""); // reset category filter
  };

  // Fetch posts whenever selected filters change
  // In the posts fetching useEffect


  // Delete action
  const deletePost = async (id) => {
    if (window.confirm("Are you sure you want to delete this post?")) {
      await api.delete(`/posts/${id}`);
      setPosts(posts.filter(p => p.id !== id));
    }
  };

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Blog Feed</h1>
      <div className="flex gap-4 mb-4">
        <select
          value={selectedCategory}
          onChange={handleCategoryChange}
          className="p-2 border rounded"
        >
          <option value="">All Categories</option>
          {categories.map(c => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>

        <select
          value={selectedTag}
          onChange={handleTagChange}
          className="p-2 border rounded"
        >
          <option value="">All Tags</option>
          {tags.map(t => (
            <option key={t.id} value={t.id}>
              {t.name}
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p>Loading posts...</p>
      ) : posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        <ul className="space-y-4">
          {posts.map(post => (
            <li key={post.id}>
              <PostCard
                post={post}
                currentUser={currentUser}
                onDelete={deletePost}
              />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
