import { useState } from 'react';
import  {apiInstance} from '../api/axios';
import { summarizeContent, generateTags } from "../api/aiService";

export default function CreatePostPage() {
  const [title, setTitle] = useState(''); 
  const [content, setContent] = useState('');
  const [aiGenerating, setAiGenerating] = useState(false);
  const [summary, setSummary] = useState("");
  const [tags, setTags] = useState([]);
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await apiInstance.post('/posts', {
        title,
        content,
        category,
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      });
      
      navigate('/', { state: { refresh: true }});
    } catch (error) {
      console.error('Error creating post:', error);
      setError(error.response?.data?.message || 'Failed to create post');
    } finally {
      setLoading(false);
    }
  };

  const handleAIGenerate = async () => {
    setAiGenerating(true);
    try {
        const summaryResult = await summarizeContent(content);
        const tagsResult = await generateTags(content);

        setSummary(summaryResult);
        setTags(tagsResult);
        const res = await apiInstance.post("/ai/generate", { title, content }); // Change api to apiInstance
        setContent(res.data.generatedContent);
    } catch {
        alert("AI failed");
    }
    setAiGenerating(false);
};
  return (
    <div className="max-w-xl mx-auto mt-6 p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">Create New Post</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          className="w-full p-2 border rounded"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <textarea
          rows="8"
          className="w-full p-2 border rounded"
          placeholder="Content"
          value={content}
          onChange={e => setContent(e.target.value)}
        />
        <div className="flex gap-4">
          <button
            type="button"
            className="bg-purple-500 text-white px-4 py-2 rounded"
            onClick={handleAIGenerate}
            disabled={aiGenerating}
          >
            {aiGenerating ? "Generating..." : "Use AI"}
          </button>
          {summary && (
          <div className="bg-gray-100 p-3 rounded mt-2">
            <strong>Suggested Summary:</strong>
            <p>{summary}</p>
          </div>
        )}

        {tags.length > 0 && (
          <div className="bg-gray-100 p-3 rounded mt-2">
            <strong>Suggested Tags:</strong>
            <p>{tags.join(", ")}</p>
          </div>
        )}
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
            Publish
          </button>
        </div>
      </form>
    </div>
  );
}
