import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authApi } from '../api/axios'; // Import authApi instead of using fetch

const RegisterPage = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear any existing error when user types
    if (error) setError('');
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Log the request details
    console.log('Registration request:', {
      url: `${authApi.defaults.baseURL}/register`,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      data: { username: form.username }
    });

    try {
      const response = await authApi.post('/register', form, {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        withCredentials: true // Add this to handle CORS
      });

      console.log('Registration response:', {
        status: response.status,
        headers: response.headers,
        data: response.data
      });

      if (response.status === 200 || response.status === 201) {
        console.log('Registration successful');
        alert('Registration successful!');
        navigate('/login');
      }
    } catch (err) {
      console.error('Registration error details:', {
        status: err.response?.status,
        statusText: err.response?.statusText,
        message: err.message,
        data: err.response?.data,
        headers: err.response?.headers
      });

      if (err.response?.status === 403) {
        setError('Server rejected the request. Please try again or contact support.');
      } else if (err.response?.status === 409) {
        setError('Username already exists.');
      } else if (!err.response) {
        setError('Network error. Please check your connection.');
      } else {
        setError(err.response?.data?.message || 'Registration failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };
  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white/10 backdrop-blur-sm rounded-lg shadow-xl">
      <h2 className="text-2xl font-bold mb-6 text-white">Register</h2>
      {error && (
        <div className="mb-4 p-3 text-red-200 bg-red-900/50 rounded-lg border border-red-500/50">
          {error}
        </div>
      )}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg 
                   text-white placeholder-gray-400 focus:outline-none 
                   focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          required
        />
        <input
          type="email"
          className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg 
                   text-white placeholder-gray-400 focus:outline-none 
                   focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg 
                   text-white placeholder-gray-400 focus:outline-none 
                   focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <button
          type="submit"
          className="w-full px-4 py-3 bg-blue-500/90 text-white rounded-lg
                   hover:bg-blue-600 transition-all duration-200
                   disabled:bg-blue-500/50 disabled:cursor-not-allowed"
          disabled={loading}
        >
          {loading ? 'Registering...' : 'Register'}
        </button>
      </form>
    </div>
  );
}
export default RegisterPage;
