import { useEffect, useState } from 'react';
import { apiInstance } from '../api/axios';
import PostCard from '../components/PostCard';
import { useNavigate } from 'react-router-dom';

export default function HomePage() {
  const [posts, setPosts] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [categories, setCategories] = useState([]);
  const [tags, setTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [selectedTag, setSelectedTag] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    const checkAuth = async () => {
      console.log("=== Auth Check ===");
      const token = localStorage.getItem("token");

      if (!token) {
        setLoading(false);
        return;
      }

      try {
        const parts = token.split('.');
        if (parts.length !== 3) throw new Error('Invalid token format');

        const payload = JSON.parse(atob(parts[1]));
        if (payload.exp && payload.exp * 1000 < Date.now()) {
          throw new Error('Token expired');
        }

        if (isMounted) {
          setCurrentUser(payload.sub);
          setIsAuthenticated(true);
          console.log("User authenticated:", payload.sub);
          await fetchData();
        }
      } catch (error) {
        console.error("Auth error:", error.message);
        localStorage.removeItem("token");
        if (isMounted) {
          setCurrentUser(null);
          setIsAuthenticated(false);
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    checkAuth();

    return () => {
      isMounted = false;
    };
  }, []);

  const fetchData = async () => {
    if (!isAuthenticated) return;
    
    setLoading(true);
    try {
      const url = "/posts";
      const postsRes = await apiInstance.get(url);

      if (postsRes?.data) {
        setPosts(postsRes.data);
        console.log('Posts fetched successfully');
        
        const uniqueCategories = [...new Set(postsRes.data
          .filter(post => post.category)
          .map(post => post.category))];
        
        const uniqueTags = [...new Set(postsRes.data
          .flatMap(post => post.tags || []))];
        
        setCategories(uniqueCategories);
        setTags(uniqueTags);
      }
    } catch (error) {
      console.error("Data fetch error:", {
        status: error.response?.status,
        message: error.message
      });
      
      if (error.response?.status === 403 || error.response?.status === 401) {
        setIsAuthenticated(false);
        localStorage.removeItem('token');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setSelectedTag("");
  };

  const handleTagChange = (e) => {
    setSelectedTag(e.target.value);
    setSelectedCategory("");
  };

  const deletePost = async (id) => {
    if (window.confirm("Are you sure you want to delete this post?")) {
      try {
        await apiInstance.delete(`/posts/${id}`);
        setPosts(posts.filter(p => p.id !== id));
      } catch (error) {
        console.error("Delete error:", error);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-3xl mx-auto px-4 py-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-900">Blog Feed</h1>
            {isAuthenticated ? (
              <div className="flex items-center gap-4">
                <span className="text-gray-600">
                  Welcome, <span className="font-medium">{currentUser}</span>
                </span>
                <button
                  onClick={() => navigate('/create')}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  New Post
                </button>
              </div>
            ) : (
              <button
                onClick={() => navigate('/login')}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Login to View Posts
              </button>
            )}
          </div>
        </div>
      </header>

      <main className="max-w-3xl mx-auto px-4 py-6">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-4 border-blue-500 border-t-transparent"></div>
          </div>
        ) : !isAuthenticated ? (
          <div className="text-center py-8 bg-white rounded-lg shadow">
            <p className="text-gray-600">Please login to view and create posts.</p>
          </div>
        ) : posts.length === 0 ? (
          <div className="text-center py-8 bg-white rounded-lg shadow">
            <p className="text-gray-600">No posts found.</p>
            <button
              onClick={() => navigate('/create')}
              className="mt-4 text-blue-500 hover:text-blue-600"
            >
              Create your first post
            </button>
          </div>
        ) : (
          <>
            {categories.length > 0 && (
              <div className="bg-white rounded-lg shadow p-4 mb-6">
                <h2 className="text-sm font-medium text-gray-700 mb-3">Filters</h2>
                <div className="flex flex-col sm:flex-row gap-4">
                  <select
                    value={selectedCategory}
                    onChange={handleCategoryChange}
                    className="p-2 border rounded flex-1 text-sm"
                  >
                    <option value="">All Categories</option>
                    {categories.map((c, index) => (
                      <option key={index} value={c}>{c}</option>
                    ))}
                  </select>

                  <select
                    value={selectedTag}
                    onChange={handleTagChange}
                    className="p-2 border rounded flex-1 text-sm"
                  >
                    <option value="">All Tags</option>
                    {tags.map((t, index) => (
                      <option key={index} value={t}>{t}</option>
                    ))}
                  </select>
                </div>
              </div>
            )}
            
            <ul className="space-y-6">
              {posts.map(post => (
                <li key={post.id} className="bg-white rounded-lg shadow">
                  <PostCard
                    post={post}
                    currentUser={currentUser}
                    onDelete={deletePost}
                  />
                </li>
              ))}
            </ul>
          </>
        )}
      </main>
    </div>
  );
}
