export default function PostCard({ post, currentUser, onDelete, onEdit, isOwner }) {
  return (
    <div className="glass-card rounded-xl overflow-hidden group">
      <div className="p-6">
        <h2 className="text-2xl font-semibold mb-3 text-white/90 group-hover:text-white
                     transition-colors duration-200">
          {post.title}
        </h2>
        <p className="text-gray-300 mb-4 line-clamp-3">{post.content}</p>

        <div className="flex flex-wrap gap-2 mb-4">
          {post.category && (
            <span className="px-3 py-1 bg-blue-500/20 text-blue-300 text-sm rounded-full">
              {post.category}
            </span>
          )}
          {post.tags?.map((tag, index) => (
            <span key={index} 
                  className="px-3 py-1 bg-gray-700/30 text-gray-300 text-sm rounded-full">
              {tag}
            </span>
          ))}
        </div>

        <div className="flex justify-between items-center pt-4 border-t border-gray-700/50">
          <span className="text-sm text-gray-400">
            By {post.author}
          </span>
          {isOwner && (
            <div className="flex gap-3">
              <button
                onClick={() => onEdit(post.id)}
                className="px-4 py-1.5 text-sm bg-blue-500/20 text-blue-300 
                         rounded-lg hover:bg-blue-500/30 transition-colors"
              >
                Edit
              </button>
              <button
                onClick={() => onDelete(post.id)}
                className="px-4 py-1.5 text-sm bg-red-500/20 text-red-300 
                         rounded-lg hover:bg-red-500/30 transition-colors"
              >
                Delete
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
