import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authApi } from '../api/axios'; // Import authApi instead of using fetch

const RegisterPage = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear any existing error when user types
    if (error) setError('');
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // authApi already has the correct configuration from axios.js
      const response = await authApi.post('/register', form);

      if (response.status === 200 || response.status === 201) {
        navigate('/login');
      }
    } catch (err) {
      console.error('Registration error:', err);
      
      if (err.response?.status === 409) {
        setError('Username already exists.');
      } else {
        setError('Registration failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
};
  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow rounded-xl border">
      <h2 className="text-2xl font-bold mb-4">Register</h2>
      {error && (
        <div className="mb-4 p-2 text-red-600 bg-red-50 rounded border border-red-200">
          {error}
        </div>
      )}
      <form onSubmit={handleSubmit} className="space-y-4">
      <input
        name="username"
        placeholder="Username"
        value={form.username}
        onChange={handleChange}
        className="w-full p-2 border rounded text-gray-800 bg-white placeholder-gray-400"
        required
      />
      <input
        name="password"
        type="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        className="w-full p-2 border rounded text-gray-800 bg-white placeholder-gray-400"
        required
      />
        <button 
          type="submit" 
          className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default RegisterPage;
